INSERT INTO `creature_ai_texts` VALUES ('-1021', 'Amaze at the wonders that the Darkmoon Faire has uncovered in this vast and mysterious world! We have spared no expense in bringing you excitement that children of all ages will delight in!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', '14823');
