INSERT INTO `db_script_string` VALUES ('2000000174', 'Thank you. The Light be with you both.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000175', 'Milord, your guest has arrived. He awaits your presence.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000176', 'Ah, thank you kindly. I will leave you to the library while I tend to this small matter.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000177', 'I shall use this time wisely, milord. Thank you.', null, null, null, null, null, null, null, null, '0', '0', '0', '2', null);
INSERT INTO `db_script_string` VALUES ('2000000178', 'It\'s time for my meditation, leave me.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000179', 'Yes, sir!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000180', '%s waits for the guards to be out of sight.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000181', 'There you are. What news from Westfall?', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000182', 'VanCleef sends word that the plans are underway. But he\'s heard rumors about someone snooping about.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000183', 'Hmm, it could be that meddler Shaw. I will see what I can discover. Be off with you. I\'ll contact you again soon.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000184', 'Very well. I will return then.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000185', 'That\'s it! That\'s what you were waiting for! KILL THEM!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000186', 'Defend yourself, fool!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000187', 'The abomination has overrun the Night Watch camp! Quickly, we must intercept it before it reaches town!', null, null, null, null, null, null, null, null, '0', '6', '7', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000188', 'The beast is slain! All is well in Darkshire.', null, null, null, null, null, null, null, null, '0', '6', '7', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000189', 'Quillboar scraps! These should do the trick. Fish love Quillboar.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000190', 'Caught one! I\'ll see if any of the guards are hungry. Come on Fang!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000191', 'I caught a fish if you are hungry...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000192', 'A nice catch it is at that! You bring honor to the Horde boy, We thank you!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000195', 'As you wish...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000196', 'I very much doubt that he will have anything to say, stranger...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000197', 'I personally didn\'t mind him. It was the Prince who took exception to a high elf in his domain. Alas, I am not one to question the Prince. In his defense, he did not incinerate Master Winthalus immediately.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000198', 'We most definitely do not need any more attention drawn to us, stranger. Return to those that sent you in search of the lost master and tell them that nothing could be found. When this is done - and I will know when it is so - return and I shall reveal the secrets of the deceased.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000202', 'Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000203', 'When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000204', 'There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000205', 'He worried that his power weakened, that soon even we last of his precious soulgems would fade and die.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000206', 'His lieutenant, Ilkurd Magthrull possesses a tome which might indicate the location of the remaining soulgems.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000207', '%s sniffs the elixir then eagerly digs in!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000208', 'It has been a long time, Bethor, my friend.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000209', 'When time permits, we must speak at length.  For we have much to discuss.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000210', 'And thank you, $N.  Without your aid I may never have found my way to the Forsaken.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000211', 'Farewell, my friend.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000212', '%s summons a messenger...', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000213', 'Go, my servant. Send word of Stonetalon to Apothecary Zamah.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000214', '%s gives message to Apothecary Zamah.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000215', 'Apothecary Zamah smiles.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000234', 'What? $N, you served me well, but...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000235', 'Why have you summoned me so soon? I haven\'t yet reached my full power!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000236', 'No matter. You were a fool to help me, and now you will pay!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000237', 'Thank again, $n. Now I\'ll just wait here until it\'s safe to leave.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000240', 'The power of the Tear of the Moons is mine! Mine I say!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000241', 'Feegly the Exiled begins to rub the Tear of the Moons.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000242', 'Power! Glorious power!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000243', 'Feegly the Exiled begins to make strange grunting noises. The Tear of the Moons drops to the ground and shatters.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000260', 'Terenthis, Raene sent me to find you. The Sentinels are in need of help in Ashenvale.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000318', 'Here\'s a beacon, $N. Keep it to yourself, if you\'re gonna find mutilated things that we need.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000349', 'Give to the charities who seek to help the victims of these hard times! Please.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'John Turner - Say 1');
INSERT INTO `db_script_string` VALUES ('2000000350', 'Don\'t forget the orphans of Stormwind!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'John Turner - Say 2');
INSERT INTO `db_script_string` VALUES ('2000000351', 'Give of your hearts and your purses! Give to the children of Stormwind who have lost their parents.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'John Turner - Say 3');
INSERT INTO `db_script_string` VALUES ('2000000352', 'Help the children of Stormwind... victims of the war and plague!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'John Turner - Say 4');
INSERT INTO `db_script_string` VALUES ('2000000359', 'He cannot be defeated. <The relic burns to nothing. The memories it held are now your own. This city was destroyed by a being not of this world.>', null, null, null, null, null, null, null, null, '0', '4', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000358', 'Leave this place. <The relic emits a white hot arc of flame. A memory has been gained: A lone Dark Iron dwarf is surrounded by seven corpses, kneeling before a monolith of flame.>', null, null, null, null, null, null, null, null, '0', '4', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000357', 'Your existence is acknowledged. <The relic turns to dust. Your head throbs with newfound wisdom. Something evil lurks in the heart of the mountain.>', null, null, null, null, null, null, null, null, '0', '4', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000355', 'Defiler... you will be punished for this incursion. <A symbol of flame radiates from the relic before it crumbles to the earth.>', null, null, null, null, null, null, null, null, '0', '4', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000356', 'Help us, outsider. <The relic crumbles to dust. A vision of eight Dark Iron dwarves performing some sort of ritual fills your head.>', null, null, null, null, null, null, null, null, '0', '4', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000353', 'You will perish here. <Your mind fills with visions of chaos and destruction.>', null, null, null, null, null, null, null, null, '0', '4', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000354', 'Leave this place. <The relic emits a white hot arc of flame. A memory has been gained: A lone Dark Iron dwarf is surrounded by seven corpses, kneeling before a monolith of flame.>', null, null, null, null, null, null, null, null, '0', '4', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000375', 'Come,  $N. The lake is around the bend.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000376', 'Stand near me,  $N. I will protect you should anything go wrong.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000377', 'Malfurion!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000378', 'Remulos,  old friend. It is good to see you once more. I knew the message would find its way to you - one way or another.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000379', 'It was shrouded in nightmares,  Malfurion. What is happening in the Dream? What could cause such atrocities?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000380', 'I fear for the worst,  old friend. Within the Dream we fight a new foe,  born of an ancient evil. Ysera\'s noble brood has fallen victim to the old whisperings. It seems as if the Nightmare has broken through the realm to find a new host on Azeroth.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000381', 'I sensed as much,  Malfurion. Dark days loom ahead.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000382', 'Aye Remulos,  prepare the mortal races.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000383', 'You have been gone too long,  Malfurion. Peace between the Children of Azeroth has become tenuous at best. What of my father? Of your brother? Have you any news?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000384', 'Cenarius fights at my side. Illidan sits atop his throne in Outland - brooding. I\'m afraid that the loss to Arthas proved to be his breaking point. Madness has embraced him,  Remulos. He replays the events in his mind a thousand times per day,  but in his mind,  he is the victor and Arthas is utterly defeated. He is too far gone,  old friend. I fear that the time may soon come that our bond is tested and it will not be as it was at the Well in Zin-Azshari. ', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000385', 'Remulos,  I am being drawn back... Tyrande... send her my love... Tell her I am safe... Tell her... Tell her I will return... Farewell...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000386', 'Farewell,  old friend... Farewell...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000387', 'Let us return to the grove,  mortal.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000395', 'Hello, Selarin. I\'m afraid I cannot help you at the moment, but perhaps some of the adventurers here in Auberdine can...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000396', 'Thank you, Terenthis. I shall remain here for as long as I can.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000397', 'Hopefully I can send a worthy few to help Ashenvale and the Sentinels.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000403', 'This is Rigger Gizelton asking for assistance escorting my caravan past Mannoroc Coven. I\'m on the road east of Shadowprey village.', null, null, null, null, null, null, null, null, '0', '6', '0', '22', 'Rigger Quest 5843 Yell');
INSERT INTO `db_script_string` VALUES ('2000000402', 'Time for the Gizelton Caravan to head on out! We\'ll be back soon but if you cannot wait, head north to Kormek\'s Hut. We open shop in about an hour.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'Rigger Gizelton leaving South End ');
INSERT INTO `db_script_string` VALUES ('2000000401', 'I am looking for some bodyguards that would like to protect the Gizelton Caravan. We are stopped on the road east of Kormek\'s Hut, north of Kolkar Centaur Village.', null, null, null, null, null, null, null, null, '0', '6', '0', '22', 'Cork Quest 5821 Yell');
INSERT INTO `db_script_string` VALUES ('2000000400', 'So sorry to leave a customer but we have places to go and people to swindle. We will be back sometime later today. Good-bye!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', 'Cork Gizelton Leaving Kormek\'s Hut');
INSERT INTO `db_script_string` VALUES ('2000000466', 'Stand Prepared! The naga won\'t wait long to press their advantage once they know we\'re alive.', null, null, null, null, null, null, null, null, '0', '0', '0', '5', '');
INSERT INTO `db_script_string` VALUES ('2000000612', '%s loosens her grasp on the journal she had been clutching.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000808', 'I thank you. $n. And my brother thanks you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005001', 'Opus wants some Cuergo\'s Gold... with worm.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005002', 'Bijou lost! SQUAWK! *cough', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005003', 'Kibler is being investigated by G.E.T.A.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005004', 'Bijou knows where treasures are hidden.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005005', 'Dragons controlling orcs!? SQUAWK!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005006', 'They all go to the Spire but none never come back... just like Bijou.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005007', 'Ask Kibler about the treasure! SQUAWK!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005008', 'SQUAWK!! *cough* *cough* I need to quit smoking!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005009', 'Well done, soldiers! At ease!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005010', 'Zug zug! Happy to help!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005011', 'Yes right away momma!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005015', 'Terenthis begins to cast a spell as you take the cloak from him.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005016', 'Go now, $N. Find Volcor... and remember, the magics protecting you will fail if you interact with anyone, save for talking.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005017', 'Go swiftly $N. My fate is in your hands.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005018', 'Ah, this must be him now... no?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005021', 'Well, whatever it is, it works quite well!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005022', 'I\'ve found that when I throw something into this water, it has improved incredibly when I retrieve it.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005023', '%s tosses the sword into the shallow pool.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005024', 'I haven\'t really figured out what it is, but there\'s something strange about the hot springs...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005025', 'Please, follow me.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005026', 'And I am determined to find out more... So if you need my help again, you know where I\'ll be.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005027', 'The Lich King\'s forces are building. It is imperative that our timetable supports his plans.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005028', 'Tomorrow we will begin training of our promising dragons, so don\'t forget your chew toys.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005029', 'Our oldest clutch of dragons are still far from maturity, but with patience and study, we are confident the dragons will soon be ready.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005031', 'I come from the land down under... where women plague and men sunder.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005032', '...And then the tauren said, \"13 INCHES!\"', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005035', 'It\'s getting away!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005057', 'Crybaby! Crybaby!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005058', 'Baby wants her dolly!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005059', 'I wonder if your dolly can swim!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005060', 'Ha ha! I have Betsy!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005061', 'I have your dolly! Nyah nyah!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005062', 'Gimmie my dolly!!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005063', 'Don\'t hurt Betsy, you meanie!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
