INSERT INTO `db_script_string` VALUES ('2000005134', 'Here we have the Cathedral of Light, the center of spiritual enlightenment here in Stormwind.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000005135', 'Yes, that is true. Paladins and Priests alike train their skills and research great truths behind the walls of the Cathedral.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000005136', 'Children if you would please follow me, we will now be going to see the keep where King Anduin Wrynn himself sits on this throne.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000005137', 'Here we have Stormwind Keep. Built upon the ruins of Stormwind Castle, which was destroyed by the Horde in the first Great War.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005138', 'When the Horde was shattered, men returned here and began to rebuild the once great city as a testament to our own survival.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005139', 'Yes, well...let\'s head on to the monument dedicated to the heroes of the two Great Wars, the Valley of Heroes. Follow Me.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000005140', 'Isn\'t it amazing, children? All who enter the city must walk beneath the watchful eyes of the greatest heroes of our lands.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005141', 'Breathtaking. Children, when we return to the school, you will each give an oral report on one of these legendary people.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005142', 'Now, take another long look before we make our way to the Holy District and the great Cathedral of Light.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000005148', 'If we reverse the Essence flows perhaps we can alter the polarity.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
