INSERT INTO `db_script_string` VALUES ('2000005236', 'People of the Horde, citizens of Orgrimmar, come, gather round and celebrate heroes of the Horde. On this day, $N, under the auspices of our glorious Warchief, laid a mortal blow against the Black Dragonflight. The brood mother, Onyxia, has been slain!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', null);
