INSERT INTO `db_script_string` VALUES ('2000005238', 'Citizens and allies of Stormwind, on this day, history has been made. $N has laid waste to that which had attempted to usurp the rule of the kingdom. Gather round and join me in honoring our heroes.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', null);
