INSERT INTO `db_script_string` VALUES ('2000005240', 'The numbers of my companions dwindles, goddess, and my own power shall soon be insufficient to hold back the demons of Felwood.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
