INSERT INTO `db_script_string` VALUES ('2000005802', 'Zarlman Two-Moons begins chanting as he mixes the well stones and ambercom before the Tribal Fire.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005803', 'The Water of the Seers is ready for your consumption, $N.', null, null, null, null, null, null, null, null, '0', '0', '3', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000360', 'Do not taint these ruins, mortal. <You are engulfed in a blinding flash of light. A creature composed entirely of flame is the only thing you can remember seeing.>', null, null, null, null, null, null, null, null, '0', '4', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000005102', 'Ograbisi needs new hat.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000521', 'I will need to focus my energies onto this knife. By doing so, I should catch some of the stronger psychic impressions associated with it.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000522', 'I... I\'ve seen enough I think... so very draining...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000523', 'A History... Hmmmm... Let me examine this for a moment.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000524', '%s skims the pages of the book.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000525', 'Amazing! I think I have found it, $N.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000526', 'Glorious, indeed, Maltrake. It looks as if my plan worked perfectly. Wouldn\'t you say so, mortal?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000527', '%s lets loose a reptilian laugh... at least you think it\'s a laugh.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000528', 'You would not have helped the legion of Blackrock had we just asked for your assistance, now would you?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000529', 'What will we do with them, master? They are of no use to us anymore.', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000530', 'SILENCE FOOL!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000531', 'They will live of only to see the fruits of their labor: the destruction and chaos that will surely ensue as the legion of Blackrock invade the gorge.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000532', 'I leave you now, mortals. Alive and with these trinkets. Maltrake! Present them with the trinkets!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000533', 'Right away, master!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000534', '%s begins to flap his massive wings faster. He is preparing for flight.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000535', 'Oh yes, the molt - do not lose it. There are those of my kin in the Burning Steppes that would craft items that only the molt of the black dragonflight could fortify.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000536', 'The legion of Blackrock comes, dwarflings! We shall scorch the earth and set fire to the heavens. None shall survive...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000540', 'Now, let\'s see... If I am correct, then... Hmmm...', null, null, null, null, null, null, null, null, '0', '0', '0', '4', null);
INSERT INTO `db_script_string` VALUES ('2000000541', 'Blue plus red... I predict...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000542', '$N, that\'s it! I have finally figured it out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000602', 'Whoa! This is way more than what I bargained for, you\'re on your own, Slim!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000261', 'Hey Nagmara! How about a mug of Dark Iron Ale?', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000262', 'Nagmara! Have any mutton left?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000263', 'Mistress, you tell Plugger that I want more of his ale. I can\'t get enough!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000264', 'Hey Nag, how about you slice me off a piece of that...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000265', 'You got it!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000266', 'Coming right up!', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000267', 'I\'ll bring it right over, baby...', null, null, null, null, null, null, null, null, '0', '0', '0', '1', null);
INSERT INTO `db_script_string` VALUES ('2000000786', '%s wildly flips through the pages of a large tome.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000787', 'The sweet irony -- Stormwind forces cut down in defense of their city, only to rise and fight alongside the Brotherhood as instruments of destruction. ', null, null, null, null, null, null, null, null, '0', '0', '0', '11', null);
INSERT INTO `db_script_string` VALUES ('2000000788', 'And should one die, two shall rise!', null, null, null, null, null, null, null, null, '0', '0', '0', '5', null);
INSERT INTO `db_script_string` VALUES ('2000000789', '%s shuffles through the books.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000790', 'Now where did I put the Venture Company formulations?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000791', 'Hey, Raven...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000792', 'Yea?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000793', 'Can we take a break? My feet are killing me and those ... those things down there are creeping me out.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000794', 'I dunno, Jill. If Klaven comes down while we\'re slacking, we may end up as one of those things!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000795', 'Klaven doesn\'t pay me enough to deal with those zombies, drones or whatever he calls \'em. I don\'t know, Jill. If Klaven comes down while we\'re slacking, we might end up as those things!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
INSERT INTO `db_script_string` VALUES ('2000000796', '*Sigh* You\'re probably right, Raven. I\'m going back on patrol....', null, null, null, null, null, null, null, null, '0', '0', '0', '0', null);
